const data = msg.payload;

// Trim les valeurs
const cleanData = {
    name: data.name.trim(),
    code: data.code.trim(),
    desc: data.desc.trim()
};

// Recherche par code (cl√© unique)
msg.collection = "organizations";
msg.operation = "findOne";
msg.payload = [
    { code: cleanData.code }
];

// Sauvegarder les donn√©es nettoy√©es
msg.cleanData = cleanData;

return msg;

msg.collection = "organizations";
msg.operation = "insertOne";
msg.topic = msg.operation;
msg.payload = [{
    name: msg.cleanData.name,
    code: msg.cleanData.code,
    desc: msg.cleanData.desc,
    createdAt: new Date(),
    updatedAt: new Date()
}];

// Garder cleanData pour la notification
return msg;

return {
    topic: msg.topic,
    highlight: "green",
    payload: `Organisation "${msg.cleanData.name}" cr√©√©e avec succ√®s`
};

const orgs = msg.payload || [];

msg.payload = orgs.map(org => ({
    _id: org._id.toString(),
    name: org.name,
    code: org.code,
    desc: org.desc
}));

return msg;

const searchTerm = flow.get("searchFilter") || "";
const page = flow.get("currentPage") || 1;
const limit = 20;
const skip = (page - 1) * limit;

msg.collection = "organizations";
msg.operation = "find";

// Construire le filtre de recherche
let filter = {};
if (searchTerm.trim() !== "") {
    filter = {
        $or: [
            { name: { $regex: searchTerm, $options: "i" } },
            { code: { $regex: searchTerm, $options: "i" } },
            { desc: { $regex: searchTerm, $options: "i" } }
        ]
    };
}

msg.payload = [
    filter,
    { 
        sort: { name: 1 },
        limit: limit,
        skip: skip
    }
];

// Sauvegarder pour la pagination
msg.paginationInfo = {
    currentPage: page,
    itemsPerPage: limit
};

return msg;

const row = msg.payload;

flow.set("selected", {
    _id: row._id,
    name: row.name,
    code: row.code,
    description: row.desc
});

msg.payload = {
    name: row.name,
    code: row.code,
    desc: row.desc || ""
};

return msg;

const existingId = msg.payload._id;

msg.collection = "organizations";
msg.operation = "updateOne";
msg.topic = msg.operation;
msg.payload = [
    { _id: existingId },  // Filtre par _id
    {
        $set: {
            name: msg.cleanData.name,
            desc: msg.cleanData.desc,
            updatedAt: new Date()
            // code ne change PAS (cl√© unique)
        }
    }
];

// Garder pour notification
msg.updatedName = msg.cleanData.name;

return msg;

return {
    topic: msg.topic,
    highlight: "blue",
    payload: `Organisation "${msg.updatedName}" mise √† jour`
};

const searchTerm = flow.get("searchFilter") || "";

msg.collection = "organizations";
msg.operation = "countDocuments";

// Utiliser le m√™me filtre que pour la recherche
let filter = {};
if (searchTerm.trim() !== "") {
    filter = {
        $or: [
            { name: { $regex: searchTerm, $options: "i" } },
            { code: { $regex: searchTerm, $options: "i" } },
            { desc: { $regex: searchTerm, $options: "i" } }
        ]
    };
}

msg.payload = [filter];
return msg;

const totalCount = msg.payload;

// Stocker dans flow context pour r√©utilisation
flow.set("totalOrganizations", totalCount);

msg.payload = {
    total: totalCount,
    pages: Math.ceil(totalCount / 20)
};
return msg;

const page = flow.get("currentPage") || 1;
const total = flow.get("totalOrganizations") || 0;
const itemsPerPage = 20;
const totalPages = Math.ceil(total / itemsPerPage);

msg.payload = `Page ${page} sur ${totalPages} (${total})`;
return msg;

const page = 1;
flow.set("currentPage", page);

msg.page = page;
msg.limit = 20;
return msg;

const currentPage = flow.get("currentPage") || 1;
const newPage = Math.max(1, currentPage - 1);

flow.set("currentPage", newPage);

msg.page = newPage;
msg.limit = 20;
return msg;

const currentPage = flow.get("currentPage") || 1;
const total = flow.get("totalOrganizations") || 0;
const itemsPerPage = 20;
const maxPage = Math.ceil(total / itemsPerPage);

const newPage = Math.min(maxPage, currentPage + 1);

flow.set("currentPage", newPage);

msg.page = newPage;
msg.limit = 20;
return msg;

const total = flow.get("totalOrganizations") || 0;
const itemsPerPage = 20;
const lastPage = Math.ceil(total / itemsPerPage);

flow.set("currentPage", lastPage);

msg.page = lastPage;
msg.limit = 20;
return msg;

const previousId = context.get('selected');

context.set('selected', msg.payload.id);

const commands = [];

if (previousId) {
    commands.push({
        payload: {
            command: 'deselectRow',
            arguments: [previousId]
        }
    });
}

commands.push({
    payload: {
        command: 'selectRow',
        arguments: [msg.payload.id]
    }
});

return [commands];

const selected = flow.get("selected");
msg.topic = "deleteOne";

if (!selected) {
    // Aucune organisation s√©lectionn√©e
    return [null, {
        topic: msg.topic,
        highlight: "orange",
        payload: `Veuillez s√©lectionner une organisation √† supprimer`
    }];  // Output 2 (erreur)
}

// Organisation s√©lectionn√©e, demander confirmation
msg.payload = {
    text: `Voulez-vous vraiment supprimer l'organisation "${selected.name}" (${selected.code}) ?`,
    color: "red"
};

msg.selected = selected;

return [{
    topic: msg.topic,
    highlight: "red",
    payload: `Voulez-vous vraiment supprimer l'organisation "${selected.name}" (${selected.code}) ?`
}, null];  // Output 1 (confirmation)

if (msg.payload != "Oui") return null;

const ObjectId = mongodb.ObjectId;
const selected = flow.get("selected");

msg.collection = "organizations";
msg.operation = "deleteOne";
msg.topic = msg.operation;
msg.payload = [
    { _id: new ObjectId(selected._id) }
];

// Garder le nom pour la notification
msg.deletedName = selected.name;

return msg;

const result = msg.payload;

if (result.deletedCount === 0) {
    // Organisation non trouv√©e (cas rare)
    return {
        topic: msg.topic,
        highlight: "red",
        payload: `Organisation introuvable`
    };
}

// Nettoyer la s√©lection
flow.set("selected", null);

// Suppression r√©ussie
return {
    topic: msg.topic,
    highlight: "green",
    payload: `Organisation "${msg.deletedName}" supprim√©e`
};

// Nettoyer la s√©lection
flow.set("selected", null);

msg.payload = {
    name: "",
    code: "",
    desc: ""
};
return msg;

const searchTerm = (msg.payload || "").trim();

// Stocker le filtre dans le flow context
flow.set("searchFilter", searchTerm);

// R√©initialiser √† la page 1 quand on filtre
flow.set("currentPage", 1);

msg.page = 1;
msg.limit = 20;

return msg;

const page = parseInt(msg.req.query.page) || 1;
const limit = parseInt(msg.req.query.limit) || 50;
const skip = (page - 1) * limit;
const search = msg.req.query.search || "";

msg.collection = "organizations";
msg.operation = "find";

let filter = {};
if (search.trim()) {
    filter = {
        $or: [
            { name: { $regex: search, $options: "i" } },
            { code: { $regex: search, $options: "i" } },
            { desc: { $regex: search, $options: "i" } }
        ]
    };
}

msg.payload = [filter, { sort: { name: 1 }, limit, skip }];
return msg;

msg.statusCode = 200;
msg.payload = {
    success: true,
    data: msg.payload.map(org => ({
        _id: org._id.toString(),
        name: org.name,
        code: org.code,
        desc: org.desc,
        createdAt: org.createdAt,
        updatedAt: org.updatedAt
    }))
};
return msg;

const ObjectId = mongodb.ObjectId;
const id = msg.req.params.id;

if (!ObjectId.isValid(id)) {
    msg.statusCode = 400;
    msg.payload = { success: false, error: "Invalid ID format" };
    return msg;
}

msg.collection = "organizations";
msg.operation = "findOne";
msg.payload = [{ _id: new ObjectId(id) }];
return msg;

msg.statusCode = 200;
msg.payload = {
    success: true,
    data: {
        _id: msg.payload._id.toString(),
        name: msg.payload.name,
        code: msg.payload.code,
        desc: msg.payload.desc,
        createdAt: msg.payload.createdAt,
        updatedAt: msg.payload.updatedAt
    }
};
return msg;

msg.statusCode = 404;
msg.payload = { success: false, error: "Organization not found" };
return msg;

const data = msg.payload;

// Validation
if (!data.name || !data.code) {
    msg.statusCode = 400;
    msg.payload = { 
        success: false, 
        error: "Fields 'name' and 'code' are required" 
    };
    return [null, msg];  // Output 2 (erreur)
}

// V√©rifier si code existe d√©j√†
msg.collection = "organizations";
msg.operation = "findOne";
msg.payload = [{ code: data.code.trim() }];
msg.newOrgData = {
    name: data.name.trim(),
    code: data.code.trim(),
    desc: (data.desc || "").trim()
};
return [msg, null];  // Output 1 (continue)

msg.statusCode = 409;
msg.payload = {
    success: false,
    error: `Organization with code '${msg.newOrgData.code}' already exists`
};
return msg;

msg.collection = "organizations";
msg.operation = "insertOne";
msg.payload = [{
    name: msg.newOrgData.name,
    code: msg.newOrgData.code,
    desc: msg.newOrgData.desc,
    createdAt: new Date(),
    updatedAt: new Date()
}];
return msg;

msg.statusCode = 201;
msg.payload = {
    success: true,
    data: { _id: msg.payload.insertedId.toString() }
};
return msg;
